typeProperties.script:
section Section1;
shared DelimitedText1 = let
  AdfDoc = AzureStorage.DataLakeContents("https://twjtest.dfs.core.chinacloudapi.cn/azurecost/upload/Detail_Shema.csv"),
  Csv = Csv.Document(AdfDoc, [Delimiter = ",", Encoding = TextEncoding.Utf8, QuoteStyle = QuoteStyle.Csv])
in
  Csv;
shared UserQuery = let
  Source = DelimitedText1
in
  Source;
